        public void getJourney(View v){
            Intent myIntent = new Intent(journeys.this, JourneySteps.class);
            TextView tv_start = findViewById(R.id.start);
            TextView tv_end = findViewById(R.id.end);
            String input_start = tv_start.getText().toString();
            String input_end = tv_end.getText().toString();
            myIntent.putExtra("start", input_start); //Optional parameters
            myIntent.putExtra("end", input_end); //Optional parameters
            journeys.this.startActivity(myIntent);
        }

