    // Menu
    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        MenuInflater inflater = getMenuInflater();
        inflater.inflate(R.menu.menu, menu);
        return true;
    }
    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        // Handle item selection
        switch (item.getItemId()) {
            case R.id.lines:
                Intent aboutIntent_lines = new Intent(journeys.this, MainActivity.class);
                startActivity(aboutIntent_lines);
                return true;
            case R.id.itineraire:
                Intent aboutIntent_iti = new Intent(journeys.this, journeys.class);
                startActivity(aboutIntent_iti);
                return true;
            default:
                return super.onOptionsItemSelected(item);
        }
    }
